export function createProductLabel(t){return{cols:[{gravity:.01},{id:"productLabel"+t.id,view:"label",css:"product_label_main_title",label:t.name}]}}export function createProductIcon(t){return{view:"template",id:"template"+t.id,borderless:!0,template:"<span class='webix_icon product_icon fas fa-"+t.iconName+"' style='font-size: 120px; color: #6c5b7b'></span>"}}export function createProductTime(t){return{padding:{bottom:10},rows:[{cols:[{view:"icon",icon:"fas fa-calendar-alt",css:"product_icon"},{view:"label",label:"На срок",css:"product_label_title"}]},{cols:[{view:"icon"},{view:"label",label:"до "+t.limitation+" мес."}]}]}}export function createProductAmountWithDeposit(t){return{padding:{bottom:10},rows:[{cols:[{view:"icon",icon:"fas fa-wallet",css:"product_icon"},{view:"label",label:"Сумма (с залогом), руб.",css:"product_label_title"}]},{cols:[{view:"icon"},{view:"label",label:"от "+webix.i18n.numberFormat(t.minAmountWithDeposit)+" до "+webix.i18n.numberFormat(t.maxAmountWithDeposit)}]}]}}export function createProductAmountWithoutDeposit(t){return{padding:{bottom:10},rows:[{cols:[{view:"icon",icon:"fas fa-wallet",css:"product_icon"},{view:"label",label:"Сумма (без залога), руб.",css:"product_label_title"}]},{cols:[{view:"icon"},{view:"label",label:"от "+webix.i18n.numberFormat(t.minAmountWithoutDeposit)+" до "+webix.i18n.numberFormat(t.maxAmountWithoutDeposit)}]}]}}export function createProductTextRateWithDeposit(t){return{padding:{bottom:10},rows:[{cols:[{view:"icon",icon:"fas fa-percent",css:"product_icon"},{view:"label",label:t.textRateWithDeposit,css:"product_label_title"}]}]}}export function createProductTextRateWithoutDeposit(t){return{rows:[{cols:[{view:"icon",icon:"fas fa-percent",css:"product_icon"},{view:"label",label:t.textRateWithoutDeposit,css:"product_label_title"}]}]}}